import React from "react";
import "./App.css";
import { useState } from "react";

export default function App() {
  const [grid, setGrid] = useState([5, 6]);

  return (
    <div className="App">
      <h1>MowGrass-IT</h1>
      <button type="button" class="btn btn-primary">
        Récupérer le sujet
      </button>

      <GetGridSizeArea />
    </div>
  );

  function updateSizeArea(size) {
    if (size[0] > 5 && size[1] > 5) {
      if (size[0] <= 15 && size[1] <= 15) {
        setGrid(size);
      }
    }
    else alert("La taille doit être supérieure à ")
  }

  function GetGridSizeArea() {
    return (
      <div>
        <p>Taille de la grille : </p>
        <div class="form-group row">
          <label for="example-number-input" class="col-2 col-form-label">
            Hauteur
          </label>
          <div class="col-1">
            <input
              class="form-control"
              type="number"
              value={grid[0]}
              onChange={(e) => updateSizeArea([e.target.value, grid[1]])}
            />
          </div>
        </div>
        <div class="form-group row">
          <label for="example-number-input" class="col-2 col-form-label">
            Largeur
          </label>
          <div class="col-1">
            <input
              class="form-control"
              type="number"
              value={grid[1]}
              onChange={(e) => updateSizeArea([grid[0], e.target.value])}
            />
          </div>
        </div>
      </div>
    );
  }
}
