import React from "react";
import "./App.css";
import { useState } from "react";

export default function App() {
  const [gridSize, setGridSize] = useState({
    x: 5,
    y: 5,
  });

  return (
    <div className="App">
      <h1>MowGrass-IT</h1>
      <button type="button" class="btn btn-primary">
        Récupérer le sujet
      </button>

      <GetGridSizeArea />
    </div>
  );

  function updateSizeArea(size) {
    if (size.x > 4 && size.y > 4) {
      if (size.x <= 15 && size.y <= 15) {
        setGridSize(size);
      } else alert("La taille doit être inférieure à 16");
    } else alert("La taille doit être supérieure à 4");
  }

  function GetGridSizeArea() {
    return (
      <div>
        <p className="textSizeGrid">Taille de la grille : </p>
        <div class="form-group row sizeForm">
          <label for="example-number-input" class="col-2 col-form-label">
            Hauteur :
          </label>
          <div>
            <input
              class="form-control"
              type="number"
              value={gridSize.x}
              onChange={(e) => updateSizeArea([e.target.value, gridSize.y])}
            />
          </div>
        </div>
        <div class="form-group row sizeForm">
          <label for="example-number-input" class="col-2 col-form-label">
            Largeur :
          </label>
          <div>
            <input
              class="form-control"
              type="number"
              value={gridSize.y}
              onChange={(e) => updateSizeArea([gridSize.x, e.target.value])}
            />
          </div>
        </div>
      </div>
    );
  }

  function DisplayGrid() {

  }
}
